import { Component, OnInit } from '@angular/core';
import { ActivatedRoute, Router } from '@angular/router';

@Component({
  selector: 'app-nav',
  templateUrl: './nav.component.html',
  styleUrls: ['./nav.component.css']
})
export class NavComponent implements OnInit {

  constructor(private router: Router) { }

  ngOnInit() {
  }
  gotoHomePage() : void{

    this.router.navigate(['/'])

    }
  gotoLyricsPage() : void{

    this.router.navigate(['/lyrics'])

    }

    gotoVisualizePage() : void{

      this.router.navigate(['/visualize'])

      }

}
<nav class="navbar navbar-dark align-items-start sidebar  sidebar-dark accordion bg-gradient-primary p-0">
    <div class="container-fluid d-flex flex-column p-0">
        <a class="navbar-brand d-flex justify-content-center align-items-center sidebar-brand m-0" href="/">
            <div class="sidebar-brand-icon rotate-n-15"><i class="fas fa-bars"></i></div>
            <div class="sidebar-brand-text mx-3"><span>Music Data</span></div>
        </a>
        <hr class="sidebar-divider my-0">
        <ul class="nav navbar-nav text-light" id="accordionSidebar">
            <li class="nav-item" role="presentation"><a (click)= "gotoHomePage()" class="nav-link active"><i class="fas fa-home"></i><span>Home</span></a></li>
            <li class="nav-item" role="presentation"><a (click)= "gotoLyricsPage()" class="nav-link"><i class="fas fa-user-circle"></i><span>Lyrics</span></a></li>
            <li class="nav-item" role="presentation"><a (click)="gotoVisualizePage()" class="nav-link"><i class="fas fa-bars"></i><span>Visualize</span></a></li>

        </ul>
        <div class="text-center d-none d-md-inline"><button class="btn rounded-circle border-0" id="sidebarToggle"
                type="button"></button></div>
    </div>
</nav>
